postCreateCommand:
bash -lc 'set -e; if ! command -v wassette >/dev/null 2>&1; then curl -fsSL https://raw.githubusercontent.com/microsoft/wassette/main/install.sh | bash; fi; mkdir -p .vscode; if [ ! -f .vscode/mcp.json ]; then cat > .vscode/mcp.json <<"JSON"
{
  "servers": {
    "wassette": {
      "type": "stdio",
      "command": "wassette",
      "args": ["serve", "--stdio"]
    }
  },
  "inputs": []
}
JSON
fi'
